INSERT INTO `article` VALUES (48, 1, 21, '1', 'mysql练习题习题是从网上找的练习题，sql语句不太熟练，可能并不是最优解，有不对的地方请大家指正表明和字段学生表 Student(s_id,s_name,s_birth,s_sex) –学生编号,', '<h1 style=\"margin: 8px 0px 16px; line-height: 36px; color: rgb(79, 79, 79); text-rendering: optimizelegibility; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; font-size: 28px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\">mysql练习题</h1><p style=\"margin-top: 0px; margin-bottom: 16px; font-size: 15px; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\">习题是从网上找的练习题，sql语句不太熟练，可能并不是最优解，有不对的地方请大家指正</p><h3 style=\"margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); text-rendering: optimizelegibility; font-size: 22px; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\"><a style=\"color: rgb(78, 161, 219); box-sizing: border-box; outline: 0px; cursor: pointer; word-break: break-all;\"></a>表明和字段</h3><ul class=\" list-paddingleft-2\" style=\"list-style-type: none;\"><li><p style=\"margin-top: 0px; margin-bottom: 15px;\">学生表 Student(s_id,s_name,s_birth,s_sex) –学生编号,学生姓名, 出生年月,学生性别</p></li><li><p style=\"margin-top: 0px; margin-bottom: 15px;\">课程表 Course(c_id,c_name,t_id) – –课程编号, 课程名称, 教师编号</p></li><li><p style=\"margin-top: 0px; margin-bottom: 15px;\">教师表 Teacher(t_id,t_name) –教师编号,教师姓名</p></li><li><p style=\"margin-top: 0px; margin-bottom: 15px;\">成绩表 Score(s_id,c_id,s_score) –学生编号,课程编号,分数</p></li></ul><h3 style=\"margin: 8px 0px 16px; line-height: 30px; color: rgb(79, 79, 79); text-rendering: optimizelegibility; font-size: 22px; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\"><a style=\"color: rgb(78, 161, 219); box-sizing: border-box; outline: 0px; cursor: pointer; word-break: break-all;\"></a>测试数据</h3><pre class=\"prettyprint\" style=\"padding: 8px 16px 4px 56px; color: rgb(68, 68, 68); border-radius: 2px; font-family: Consolas, Inconsolata, Courier, monospace; margin-top: 0px; margin-bottom: 24px; font-size: 14px; line-height: 22px; word-break: break-all; overflow-wrap: break-word; white-space: pre-wrap; background-color: rgb(246, 248, 250); border: none; overflow: auto hidden; text-indent: 30px; box-sizing: border-box; outline: 0px; position: relative;\">--建表--学生表CREATE&nbsp;TABLE&nbsp;`Student`(&nbsp;&nbsp;&nbsp;&nbsp;`s_id`&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`s_name`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;`s_birth`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;`s_sex`&nbsp;VARCHAR(10)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`s_id`)&nbsp;);--课程表CREATE&nbsp;TABLE&nbsp;`Course`(&nbsp;&nbsp;&nbsp;&nbsp;`c_id`&nbsp;&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`c_name`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;`t_id`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL,&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`c_id`)&nbsp;);--教师表CREATE&nbsp;TABLE&nbsp;`Teacher`(&nbsp;&nbsp;&nbsp;&nbsp;`t_id`&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`t_name`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`t_id`)&nbsp;);--成绩表CREATE&nbsp;TABLE&nbsp;`Score`(&nbsp;&nbsp;&nbsp;&nbsp;`s_id`&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`c_id`&nbsp;&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`s_score`&nbsp;INT(3),&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`s_id`,`c_id`)&nbsp;);--插入学生表测试数据insert&nbsp;into&nbsp;Student&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;赵雷&#39;&nbsp;,&nbsp;&#39;1990-01-01&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;钱电&#39;&nbsp;,&nbsp;&#39;1990-12-21&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;孙风&#39;&nbsp;,&nbsp;&#39;1990-05-20&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;04&#39;&nbsp;,&nbsp;&#39;李云&#39;&nbsp;,&nbsp;&#39;1990-08-06&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;05&#39;&nbsp;,&nbsp;&#39;周梅&#39;&nbsp;,&nbsp;&#39;1991-12-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;06&#39;&nbsp;,&nbsp;&#39;吴兰&#39;&nbsp;,&nbsp;&#39;1992-03-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;07&#39;&nbsp;,&nbsp;&#39;郑竹&#39;&nbsp;,&nbsp;&#39;1989-07-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;08&#39;&nbsp;,&nbsp;&#39;王菊&#39;&nbsp;,&nbsp;&#39;1990-01-20&#39;&nbsp;,&nbsp;&#39;女&#39;);--课程表测试数据insert&nbsp;into&nbsp;Course&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;语文&#39;&nbsp;,&nbsp;&#39;02&#39;);insert&nbsp;into&nbsp;Course&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;数学&#39;&nbsp;,&nbsp;&#39;01&#39;);insert&nbsp;into&nbsp;Course&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;英语&#39;&nbsp;,&nbsp;&#39;03&#39;);--教师表测试数据insert&nbsp;into&nbsp;Teacher&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;张三&#39;);insert&nbsp;into&nbsp;Teacher&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;李四&#39;);insert&nbsp;into&nbsp;Teacher&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;王五&#39;);--成绩表测试数据insert&nbsp;into&nbsp;Score&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;80);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;90);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;99);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;70);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;60);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;80);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;80);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;80);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;80);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;04&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;50);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;04&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;30);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;04&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;20);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;05&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;76);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;05&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;87);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;06&#39;&nbsp;,&nbsp;&#39;01&#39;&nbsp;,&nbsp;31);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;06&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;34);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;07&#39;&nbsp;,&nbsp;&#39;02&#39;&nbsp;,&nbsp;89);insert&nbsp;into&nbsp;Score&nbsp;values(&#39;07&#39;&nbsp;,&nbsp;&#39;03&#39;&nbsp;,&nbsp;98);12345678910111213141516171819202122232425262728293031323334353637383940414243444546474849505152535455565758596061626364656667</pre><p></p><p style=\"margin-top: 0px; margin-bottom: 16px; font-size: 15px; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\">查询”01”课程比”02”课程成绩高的学生的信息及课程分数</p><pre class=\"prettyprint\" style=\"padding: 8px 16px 4px 56px; color: rgb(68, 68, 68); border-radius: 2px; font-family: Consolas, Inconsolata, Courier, monospace; margin-top: 0px; margin-bottom: 24px; font-size: 14px; line-height: 22px; word-break: break-all; overflow-wrap: break-word; white-space: pre-wrap; background-color: rgb(246, 248, 250); border: none; overflow: auto hidden; text-indent: 30px; box-sizing: border-box; outline: 0px; position: relative;\">SELECT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a.*,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;b.s_score&nbsp;01&nbsp;_score,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c.s_score&nbsp;02&nbsp;_score&nbsp;&nbsp;FROM&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;student&nbsp;a&nbsp;&nbsp;&nbsp;&nbsp;JOIN&nbsp;score&nbsp;b&nbsp;ON&nbsp;a.s_id&nbsp;=&nbsp;b.s_id&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AND&nbsp;b.c_id&nbsp;=&nbsp;&#39;01&#39;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;LEFT&nbsp;JOIN&nbsp;score&nbsp;c&nbsp;ON&nbsp;a.s_id&nbsp;=&nbsp;c.s_id&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AND&nbsp;c.c_id&nbsp;=&nbsp;&#39;02&#39;&nbsp;WHERE&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;b.s_score&nbsp;&gt;&nbsp;c.s_score123456789101112</pre><p></p><p style=\"margin-top: 0px; margin-bottom: 16px; font-size: 15px; text-indent: 30px; white-space: normal; box-sizing: border-box; outline: 0px; padding: 0px; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; word-break: break-all; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; background-color: rgb(255, 255, 255);\">查询平均成绩大于等于60分的同学的学生编号和学生姓名和平均成绩</p><pre class=\"prettyprint\" style=\"padding: 8px 16px 4px 56px; color: rgb(68, 68, 68); border-radius: 2px; font-family: Consolas, Inconsolata, Courier, monospace; margin-top: 0px; margin-bottom: 24px; font-size: 14px; line-height: 22px; word-break: break-all; overflow-wrap: break-word; white-space: pre-wrap; background-color: rgb(246, 248, 250); border: none; overflow: auto hidden; text-indent: 30px; box-sizing: border-box; outline: 0px; position: relative;\">SELECT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a.s_id,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a.s_name,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;round(&nbsp;AVG(&nbsp;b.s_score&nbsp;),&nbsp;1&nbsp;)&nbsp;AS&nbsp;avg&nbsp;FROM&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;student&nbsp;a&nbsp;&nbsp;&nbsp;&nbsp;JOIN&nbsp;score&nbsp;b&nbsp;ON&nbsp;a.s_id&nbsp;=&nbsp;b.s_id&nbsp;&nbsp;GROUP&nbsp;BY&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;b.s_id&nbsp;&nbsp;HAVING&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;avg&nbsp;&gt;=&nbsp;&#39;60&#39;1234567891011</pre><p></p><p><br/> </p>', '123', '12', 'http://localhost:9000/static/storage/32bwro9o4c1xno7db78s.jpeg', 1, 0, 0, NULL, NULL, '2019-03-21 20:22:07', '2019-03-27 17:26:05');
