ULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;`t_id`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL,&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`c_id`)&nbsp;);--教师表CREATE&nbsp;TABLE&nbsp;`Teacher`(&nbsp;&nbsp;&nbsp;&nbsp;`t_id`&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`t_name`&nbsp;VARCHAR(20)&nbsp;NOT&nbsp;NULL&nbsp;DEFAULT&nbsp;&#39;&#39;,&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`t_id`)&nbsp;);--成绩表CREATE&nbsp;TABLE&nbsp;`Score`(&nbsp;&nbsp;&nbsp;&nbsp;`s_id`&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`c_id`&nbsp;&nbsp;VARCHAR(20),&nbsp;&nbsp;&nbsp;&nbsp;`s_score`&nbsp;INT(3),&nbsp;&nbsp;&nbsp;&nbsp;PRIMARY&nbsp;KEY(`s_id`,`c_id`)&nbsp;);--插入学生表测试数据insert&nbsp;into&nbsp;Student&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;赵雷&#39;&nbsp;,&nbsp;&#39;1990-01-01&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;钱电&#39;&nbsp;,&nbsp;&#39;1990-12-21&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;03&#39;&nbsp;,&nbsp;&#39;孙风&#39;&nbsp;,&nbsp;&#39;1990-05-20&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;04&#39;&nbsp;,&nbsp;&#39;李云&#39;&nbsp;,&nbsp;&#39;1990-08-06&#39;&nbsp;,&nbsp;&#39;男&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;05&#39;&nbsp;,&nbsp;&#39;周梅&#39;&nbsp;,&nbsp;&#39;1991-12-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;06&#39;&nbsp;,&nbsp;&#39;吴兰&#39;&nbsp;,&nbsp;&#39;1992-03-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;07&#39;&nbsp;,&nbsp;&#39;郑竹&#39;&nbsp;,&nbsp;&#39;1989-07-01&#39;&nbsp;,&nbsp;&#39;女&#39;);insert&nbsp;into&nbsp;Student&nbsp;values(&#39;08&#39;&nbsp;,&nbsp;&#39;王菊&#39;&nbsp;,&nbsp;&#39;1990-01-20&#39;&nbsp;,&nbsp;&#39;女&#39;);--课程表测试数据insert&nbsp;into&nbsp;Course&nbsp;values(&#39;01&#39;&nbsp;,&nbsp;&#39;语文&#39;&nbsp;,&nbsp;&#39;02&#39;);insert&nbsp;into&nbsp;Course&nbsp;values(&#39;02&#39;&nbsp;,&nbsp;&#39;数学&#39;&nbsp;,&nbsp;&#39;01&#39;);insert&nbsp;into&nbsp;C